import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { Title } from '@angular/platform-browser';
import { ActivatedRoute, Router } from '@angular/router';
import { first } from 'rxjs/operators';
import { AuthenticationService } from 'src/app/core/service/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent implements OnInit {

  loading: boolean = false;
  returnUrl: string = '/dashboard/ecommerce';
  loginForm!: FormGroup;
  formSubmitted: boolean = false;
  error: string = '';

  showPassword: boolean = false;

  constructor (
    private route: ActivatedRoute,
    private router: Router,
    private authenticationService: AuthenticationService,
    private fb: FormBuilder,
    private titleService: Title
  ) {
    titleService.setTitle("Log In | JobsMonday - Responsive Angular and Bootstrap 5 Admin")
  }

  ngOnInit(): void {
    this.loginForm = this.fb.group({
      email: ['employer@jobsmonday.com', [Validators.required, Validators.email]],
      password: ['1234', Validators.required]
    });

    // reset login status
    this.authenticationService.logout();


    // get return url from route parameters or default to '/'

    this.returnUrl = this.route.snapshot.queryParams['returnUrl'] || '/dashboard/employer';

  }

  /**
  * convenience getter for easy access to form fields
  */
  get formValues() { return this.loginForm.controls; }


  /**
   * On submit form
   */
  onSubmit(): void {
    this.formSubmitted = true;
    //if (this.loginForm.valid) {
      this.loading = true;
      this.authenticationService.login(this.formValues.email?.value, this.formValues.password?.value)
      this.router.navigate([this.returnUrl]);
        // .pipe(first())
        // .subscribe(
        //   (data: any) => {
        //     this.router.navigate([this.returnUrl]);
        //   },
        //   (error: any) => {
        //     this.error = error;
        //     this.loading = false;
        //   });
    //}
  }


}
<app-default-layout>
  <div class="auth-logo mx-auto">
    <a
      [routerLink]="['../../dashboard/ecommerce']"
      class="logo logo-dark text-center"
    >
      <h1 class="h5 mb-0 mt-3" i18n>JobsMonday</h1>
    </a>

    <a
      [routerLink]="['../../dashboard/ecommerce']"
      class="logo logo-light text-center"
    >
      <span class="logo-lg">
        <img src="../assets/images/logo-light.png" alt="" height="24" />
      </span>
    </a>
  </div>
  <div class="text-center">
    <p class="text-muted mt-1 mb-4" i18n>Log in to JobsMonday</p>
  </div>

  <form
    name="login-form"
    [formGroup]="loginForm"
    (ngSubmit)="onSubmit()"
    class="authentication-form"
  >
    <app-ui-preloader [display]="loading"></app-ui-preloader>
    <ngb-alert type="danger" *ngIf="error" [dismissible]="false">{{
      error
    }}</ngb-alert>

    <div class="mb-3">
      <label class="form-label" i18n>Email Address</label>
      <div class="input-group">
        <span class="input-group-text">
          <i class="icon-dual" data-feather="mail" appFeatherIcon></i>
        </span>
        <input
          type="email"
          class="form-control"
          id="email"
          placeholder="JobsMonday@site.com"
          formControlName="email"
          [ngClass]="{
            'is-invalid': formSubmitted && formValues.email.invalid
          }"
          required
        />

        <div
          *ngIf="formSubmitted && formValues.email.invalid"
          class="invalid-feedback"
        >
          <div *ngIf="formValues.email.errors?.required" i18n>
            Email is required.
          </div>
          <div *ngIf="formValues.email.errors?.email" i18n>
            Email must be a valid Email Address
          </div>
        </div>
      </div>
    </div>

    <div class="mb-3">
      <label class="form-label" i18n>Password</label>
      <a
        [routerLink]="['../recover-password']"
        class="float-end text-muted text-unline-dashed ms-1"
        >Forgot your password?</a
      >
      <div class="input-group">
        <span class="input-group-text">
          <i class="icon-dual" data-feather="lock"></i>
        </span>
        <input
          type="password"
          class="form-control"
          id="password"
          i18n-placeholder
          placeholder="Enter your password"
          formControlName="password"
          required
          minlength="4"
          [ngClass]="{
            'is-invalid': formSubmitted && formValues.password.invalid
          }"
        />

        <div
          *ngIf="formSubmitted && formValues.password.invalid"
          class="invalid-feedback"
        >
          <div *ngIf="formValues.password.errors?.required" i18n>
            Password is required.
          </div>
          <div *ngIf="formValues.password.errors?.minlength" i18n>
            Password length must be greater than 3.
          </div>
        </div>
      </div>
    </div>

    <div class="mb-3">
      <div class="form-check">
        <input
          type="checkbox"
          class="form-check-input"
          id="checkbox-signin"
          checked
        />
        <label class="form-check-label" for="checkbox-signin" i18n
          >Remember me</label
        >
      </div>
    </div>

    <div class="mb-3 text-center d-grid">
      <button class="btn btn-primary" type="submit" i18n>Log In</button>
    </div>
    <p class="text-muted" footerLink i18n>
      Don't have an account?
      <a [routerLink]="['../register']" class="text-primary fw-bold ms-1"
        >Sign Up</a
      >
    </p>
  </form>
  <div class="py-3 text-center">
    <span class="fs-16 fw-bold">Downloads</span>
  </div>
  <div class="row">
    <!-- <div class="col-12 text-center">
      <a href="" class="btn btn-white mb-2 mb-sm-0 me-1"
        ><i class="uil uil-google icon-google me-2"></i>PlayStore</a
      >
      <a href="" class="btn btn-white mb-2 mb-sm-0"
        ><i class="uil uil-facebook me-2 icon-fb"></i>AppStore</a
      >
    </div> -->

    <div class="col-12 text-center">
      <a href="" class="btn btn-white mb-2 mb-sm-0 me-1"><i></i>PlayStore</a>
      <a href="" class="btn btn-white mb-2 mb-sm-0"><i></i>AppStore</a>
    </div>
  </div>
</app-default-layout>
