import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { Router } from '@angular/router';
import { MenuItem } from '../../shared/models/menu.model';

@Component({
  selector: 'app-icon-menu',
  templateUrl: './icon-menu.component.html',
  styleUrls: ['./icon-menu.component.scss']
})
export class IconMenuComponent implements OnInit {


  @Input() menuItems: MenuItem[] = [];
  @Input() activeMenuItems: string[] = [];
  @Input() openMenuItems: string[] = [];

  @Output() toggleMenu = new EventEmitter<{ menuItem: MenuItem }>();

  constructor (private router: Router) { }

  ngOnInit(): void {
  }

  /**
 * toggles open menu
 * @param menuItem clicked menuitem
 * @param collapse collpase instance
 */
  toggleMenuItem(event: any, menuItem: MenuItem): void {
    event.preventDefault();
    if (!menuItem.children) {
      this.router.navigate([menuItem.link]);
    }
    this.toggleMenu.emit({ menuItem: menuItem });
  }


}
<ngx-simplebar class="sidebar-icon-menu h-100">
  <!-- LOGO -->
  <a [routerLink]="'../dashboard/ecommerce'" class="logo">
    <span>
      <!-- <img src="../assets/images/logo-sm-light.png" alt="" height="28" /> -->
      <h1 class="h5 mb-0 mt-3" i18n>JobsMonday</h1>
    </span>
  </a>

  <nav class="nav flex-column" id="two-col-sidenav-main">
    <ng-container *ngFor="let menu of menuItems">
      <a
        class="nav-link two-column-nav-link-ref"
        href="javascript: void(0)"
        [attr.data-menu-key]="menu.key"
        [ngClass]="{ active: openMenuItems.includes(menu.key!) }"
        [ngbTooltip]="menu.label"
        placement="end"
        container="body"
        (click)="toggleMenuItem($event, menu)"
        *ngIf="menu.children"
      >
        <i [attr.data-feather]="menu.icon" appFeatherIcon *ngIf="menu.icon"></i>
      </a>
      <a
        class="nav-link two-column-nav-link-ref"
        [attr.href]="menu.link"
        [attr.data-menu-key]="menu.key"
        [ngClass]="{ active: openMenuItems.includes(menu.key!) }"
        [ngbTooltip]="menu.label"
        placement="end"
        container="body"
        (click)="toggleMenuItem($event, menu)"
        *ngIf="!menu.children"
      >
        <i [attr.data-feather]="menu.icon" appFeatherIcon *ngIf="menu.icon"></i>
      </a>
    </ng-container>
  </nav>
</ngx-simplebar>
